import './App.css';
import React from 'react';
import styles from './App.css';
let tab_hats = [require("./clothes/hat1.png"), require("./clothes/hat2.png"), require("./clothes/hat3.png"), require("./clothes/hat4.png"), require("./clothes/hat5.png")]
let tab_tops = [require("./clothes/top1.png"), require("./clothes/top2.png"), require("./clothes/top3.png"), require("./clothes/top4.png"), require("./clothes/top5.png")]
let tab_pants = [require("./clothes/pants1.png"), require("./clothes/pants2.png"), require("./clothes/pants3.png"), require("./clothes/pants4.png"), require("./clothes/pants5.png")]
let tab_shoes = [require("./clothes/shoes1.png"), require("./clothes/shoes2.png"), require("./clothes/shoes3.png"), require("./clothes/shoes4.png"), require("./clothes/shoes5.png")]
let indexHatLeft = 0;
let indexHatMain = 1;
let indexHatRight = 2;
let indexTopLeft = 0;
let indexTopMain = 1;
let indexTopRight = 2;
let indexPantsLeft = 0;
let indexPantsMain = 1;
let indexPantRight = 2;
let indexShoesLeft = 0;
let indexShoesMain = 1;
let indexShoesRight = 2;
function App() {
  return (
    <div className='App'>
        <div className="parent1">
            <div className="header"><h1>E-Wardrobe</h1></div>
            <div className="body">
                <div className="mainhat"><img id="mainhat" src={tab_hats[indexHatMain]}/></div>
                <div className="mainshirt"><img id="mainshirt" src={tab_tops[indexTopMain]}/></div>
                <div className="mainpants"><img id="mainpants" src={tab_pants[indexPantsMain]}/></div>
                <div className="mainshoes"><img id="mainshoes" src={tab_shoes[indexShoesMain]}/></div>
                <div className="lefthat"><img id="lefthat" className="imgside" src={tab_hats[indexHatLeft]}/></div>
                <div className="leftshirt"><img id="leftshirt" className="imgside" src={tab_tops[indexTopLeft]}/></div>
                <div className="leftpants"><img id="leftpants" className="imgside" src={tab_pants[indexPantsLeft]}/></div>
                <div className="leftshoes"><img id="leftshoes" className="imgside" src={tab_shoes[indexShoesLeft]}/></div>
                <div className="righthat"><img id="righthat" className="imgside" src={tab_hats[indexHatRight]}/></div>
                <div className="rightshirt"><img id="rightshirt" className="imgside"  src={tab_tops[indexTopRight]}/></div>
                <div className="rightpants"><img id="rightpants" className="imgside" src={tab_pants[indexPantRight]}/></div>
                <div className="rightshoes"><img id="rightshoes" className="imgside" src={tab_shoes[indexShoesRight]}/></div>
            </div>
            <div className="left">
                <div className="div1"><button onClick={decrementHats}>&lt;</button></div>
                <div className="div2"><button onClick={decrementTops}>&lt;</button></div>
                <div className="div3"><button onClick={decrementPants}>&lt;</button></div>
                <div className="div4"><button onClick={decrementShoes}>&lt;</button></div>
            </div>
            <div className="right">
                <div className="div1"><button onClick={incrementHats}>&gt;</button></div>
                <div className="div2"><button onClick={incrementTops}>&gt;</button></div>
                <div className="div3"><button onClick={incrementPants}>&gt;</button></div>
                <div className="div4"><button onClick={incrementShoes}>&gt;</button></div>
            </div>
        </div>
        <button onClick={addItem}>Add a clothe</button>
    </div>
  );
}

function addItem(){
    alert("Do you really want to add a clothe in your  E-Wardrobe ?")
}

function incrementHats() {
    if (indexHatMain < tab_hats.length-1) {
        indexHatMain++;
    } else {
        indexHatMain = 0;
    }
    if (indexHatLeft < tab_hats.length-1) {
        indexHatLeft++;
    } else {
        indexHatLeft = 0;
    }
    if (indexHatRight< tab_hats.length-1) {
        indexHatRight++;
    } else {
        indexHatRight = 0;
    }
    var element = document.getElementById("mainhat")
    element.setAttribute('src', tab_pants[indexHatMain]);
    element = document.getElementById("lefthat")
    element.setAttribute('src', tab_pants[indexHatLeft]);
    element = document.getElementById("righthat")
    element.setAttribute('src', tab_pants[indexHatRight]);
}

function decrementHats() {
    if (indexHatMain > 0) {
        indexHatMain--;
    } else {
        indexHatMain = tab_hats.length-1;
    }
    if (indexHatLeft > 0) {
        indexHatLeft--;
    } else {
        indexHatLeft = tab_hats.length-1;
    }
    if (indexHatRight > 0) {
        indexHatRight--;
    } else {
        indexHatRight = tab_hats.length-1;
    }
    var element = document.getElementById("mainhat")
    element.setAttribute('src', tab_pants[indexHatMain]);
    element = document.getElementById("lefthat")
    element.setAttribute('src', tab_pants[indexHatLeft]);
    element = document.getElementById("righthat")
    element.setAttribute('src', tab_pants[indexHatRight]);
}

function incrementTops() {
    if (indexTopMain < tab_tops.length-1) {
        indexTopMain++;
    } else {
        indexTopMain = 0;
    }
    if (indexTopLeft < tab_tops.length-1) {
        indexTopLeft++;
    } else {
        indexTopLeft = 0;
    }
    if (indexTopRight < tab_tops.length-1) {
        indexTopRight++;
    } else {
        indexTopRight = 0;
    }
    var element = document.getElementById("mainshirt")
    element.setAttribute('src', tab_tops[indexTopMain]);
    element = document.getElementById("leftshirt")
    element.setAttribute('src', tab_tops[indexTopRight]);
