import './App.css';
import React from 'react';
import styles from './App.css';
let tab_hats = [require("./clothes/hat1.png"), require("./clothes/hat2.png"), require("./clothes/hat3.png"), require("./clothes/hat4.png"), require("./clothes/hat5.png")]
let tab_tops = [require("./clothes/top1.png"), require("./clothes/top2.png"), require("./clothes/top3.png"), require("./clothes/top4.png"), require("./clothes/top5.png")]
let tab_pants = [require("./clothes/pants1.png"), require("./clothes/pants2.png"), require("./clothes/pants3.png"), require("./clothes/pants4.png"), require("./clothes/pants5.png")]
let tab_shoes = [require("./clothes/shoes1.png"), require("./clothes/shoes2.png"), require("./clothes/shoes3.png"), require("./clothes/shoes4.png"), require("./clothes/shoes5.png")]
let indexHatLeft = 0;
let indexHatMain = 1;
let indexHatRight = 2;
let indexTopLeft = 0;
let indexTopMain = 1;
let indexTopRight = 2;
let indexPantsLeft = 0;
let indexPantsMain = 1;
let indexPantRight = 2;
let indexShoesLeft = 0;
let indexShoesMain = 1;
let indexShoesRight = 2;
function App() {
  return (
    <div className='App'>
        <div className="parent1">
            <div className="header"><h1>E-Wardrobe</h1></div>
            <div className="body">
                <div className="mainhat"><img src={tab_hats[indexHatMain]}/></div>
                <div className="mainshirt"><img src={tab_hats[indexTopMain]}/></div>
                <div className="mainpants"><img id="mainpants" src={tab_pants[indexPantsMain]}/></div>
                <div className="mainshoes"><img id="mainshoes" src={require("./clothes/shoes1.png")}/></div>
                <div className="lefthat"><img className="imgside" src={require("./clothes/hat2.png")}/></div>
                <div className="leftshirt"><img className="imgside" src={require("./clothes/top2.png")}/></div>
                <div className="leftpants"><img id="leftpants" className="imgside" src={tab_pants[indexPantsLeft]}/></div>
                <div className="leftshoes"><img  className="imgside" src={require("./clothes/shoes1.png")}/></div>
                <div className="righthat"><img  className="imgside" src={require("./clothes/hat1.png")}/></div>
                <div className="rightshirt"><img className="imgside"  src={require("./clothes/top3.png")}/></div>
                <div className="rightpants"><img id="rightpants" className="imgside" src={tab_pants[indexPantRight]}/></div>
                <div className="rightshoes"><img  className="imgside" src={require("./clothes/shoes1.png")}/></div>
            </div>
            <div className="left">
                <div className="div1"></div>
                <div className="div2"></div>
                <div className="div3"><button onClick={decrementPants}>&lt;</button></div>
                <div className="div4"></div>
            </div>
            <div className="right">
                <div className="div1"></div>
                <div className="div2"></div>
                <div className="div3"><button onClick={incrementPants}>&gt;</button></div>
                <div className="div4"></div>
            </div>
        </div>
        <button onClick={addItem}>Add a clothe</button>
    </div>
  );
}

function addItem(){
    alert("Do you really want to add a clothe in your  E-Wardrobe ?")
}

function incrementPants() {
    if (indexPantsMain < tab_pants.length-1) {
        indexPantsMain++;
    } else {
        indexPantsMain = 0;
    }
    if (indexPantsLeft < tab_pants.length-1) {
        indexPantsLeft++;
    } else {
        indexPantsLeft = 0;
    }
    if (indexPantRight< tab_pants.length-1) {
        indexPantRight++;
    } else {
        indexPantRight = 0;
    }
    console.log(indexPantsMain);
    var element = document.getElementById("mainpants")
    element.setAttribute('src', tab_pants[indexPantsMain]);
    element = document.getElementById("leftpants")
    element.setAttribute('src', tab_pants[indexPantsLeft]);
    element = document.getElementById("rightpants")
    element.setAttribute('src', tab_pants[indexPantRight]);
}

function decrementPants() {
    if (indexPantsMain > 0) {
        indexPantsMain--;
    } else {
        indexPantsMain = tab_pants.length-1;
    }
    if (indexPantsLeft > 0) {
        indexPantsLeft--;
    } else {
        indexPantsMain = tab_pants.length-1;
    }
    if (indexPantRight< tab_pants.length-1) {
        indexPantRight--;
    } else {
        indexPantsMain = tab_pants.length-1;
    }
    console.log(indexPantsMain);
    var element = document.getElementById("mainpants")
    element.setAttribute('src', tab_pants[indexPantsMain]);
    element = document.getElementById("leftpants")
    element.setAttribute('src', tab_pants[indexPantsLeft]);
    element = document.getElementById("rightpants")
    element.setAttribute('src', tab_pants[indexPantRight]);
}

export default App;
