import './App.css';
import React from 'react';
import styles from './App.css';
let tab_pants = [require("./clothes/pants1.png"), require("./clothes/pants2.png"), require("./clothes/pants3.png"), require("./clothes/pants4.png"), require("./clothes/pants5.png")]
let indexLeft = 0;
let index = 1;
let indexRight = 2;
function App() {
  return (
    <div className='App'>
        <div className="parent1">
            <div className="header"><h1>E-Wardrobe</h1></div>
            <div className="body">
                <div className="mainhat"><img src={require("./clothes/hat1.png")}/></div>
                <div className="mainshirt"><img src={require("./clothes/tshirt1.png")}/></div>
                <div className="mainpants"><img id="mainpants" src={tab_pants[index]}/></div>
                <div className="mainshoes"><img src={require("./clothes/shoes1.png")}/></div>
                <div className="lefthat"><img className="imgside" src={require("./clothes/hat2.png")}/></div>
                <div className="leftshirt"><img className="imgside" src={require("./clothes/tshirt2.png")}/></div>
                <div className="leftpants"><img id="leftpants" className="imgside" src={tab_pants[indexLeft]}/></div>
                <div className="leftshoes"><img  className="imgside" src={require("./clothes/shoes1.png")}/></div>
                <div className="righthat"><img  className="imgside" src={require("./clothes/hat1.png")}/></div>
                <div className="rightshirt"><img className="imgside"  src={require("./clothes/tshirt1.png")}/></div>
                <div className="rightpants"><img id="rightpants" className="imgside" src={tab_pants[indexRight]}/></div>
                <div className="rightshoes"><img  className="imgside" src={require("./clothes/shoes1.png")}/></div>
            </div>
            <div className="left">GAUCHE</div>
            <div className="right">
                <div className="div1"></div>
                <div className="div2"><button onClick={decrementPants}>&lt"</button></div>
                <div className="div3"><button onClick={incrementPants}>></button></div>
            </div>
                <div className="div4"></div>
        </div>
        <button onClick={addItem}>Add a clothe</button>
    </div>
  );
}

function addItem(){
    alert("Do you really want to add a clothe in your  E-Wardrobe ?")
}

function incrementPants() {
    if (index < tab_pants.length-1) {
        index++;
    } else {
        index = 0;
    }
    if (indexLeft < tab_pants.length-1) {
        indexLeft++;
    } else {
        indexLeft = 0;
    }
    if (indexRight< tab_pants.length-1) {
        indexRight++;
    } else {
        indexRight = 0;
    }
    console.log(index);
    var element = document.getElementById("mainpants")
    element.setAttribute('src', tab_pants[index]);
    element = document.getElementById("leftpants")
    element.setAttribute('src', tab_pants[indexLeft]);
    element = document.getElementById("rightpants")
    element.setAttribute('src', tab_pants[indexRight]);
}

function decrementPants() {
    if (index > 0) {
        index--;
    } else {
        index = tab_pants.length-1;
    }
    if (indexLeft > 0) {
        indexLeft--;
    } else {
        index = tab_pants.length-1;
    }
    if (indexRight< tab_pants.length-1) {
        indexRight--;
    } else {
        index = tab_pants.length-1;
    }
    console.log(index);
    var element = document.getElementById("mainpants")
    element.setAttribute('src', tab_pants[index]);
    element = document.getElementById("leftpants")
    element.setAttribute('src', tab_pants[indexLeft]);
    element = document.getElementById("rightpants")
    element.setAttribute('src', tab_pants[indexRight]);
}

export default App;
